const dayjs = require('dayjs')

function birthday(date){
    const birthday = dayjs(date)
    const today = dayjs()

    const ageInYears = today.diff(birthday,'year')
    const newBirthday = birthday.add(ageInYears+1,'year')
    const daysBirthday = newBirthday.diff(today,'day')+1

    console.log(`idade: ${ageInYears}`)
    console.log(`novo aniv√©rsario ${newBirthday.format("DD/MM/YYYY")}`)
    console.log(`A quantidade de dias para o procimo aniversario e ${daysBirthday} dias`)
}

birthday('2005-11-21')